import React, { useState } from 'react';

function App({ items }) {
  const [create, setCreate] = useState(false);
  const [read, setRead] = useState(false);
  const [update, setUpdate] = useState(false);
  const [remove, setRemove] = useState(false);

  const handleRemove = (idToFRemove) => {
    const updatedItems = items.filter(item=> item.id !==  idToFRemove )
    setItems(updatedItems)
    setRemove(!remove);
  }

  const handleUpdate = () => {
    setUpdate(!update);
  }

  const handleCreate = (event) => {
    event.preventDefault();
    const id = parseInt(event.target.elements.id.value);
    const name = event.target.elements.name.value;
    const email = event.target.elements.email.value;
    const category = event.target.elements.category.value;
    const newItem = { id, name, email, category };
    items.push(newItem); // Update the items array
    setCreate(!create); // Toggle the value of create
  }

  const handleRead = () => {
    setRead(!read);
  }

  return (
    <div className="App">
      <header>
        <div className="App">
          <button onClick={() => setCreate(!create)}>{create ? "Hide" : "Show"} Heading</button>
          <button onClick={handleRead}>Read</button>
          <button onClick={handleUpdate}>Update</button>
          <button onClick={handleRemove}>Delete</button>
        </div>
      </header>

      {create && (
        <div>
          <form onSubmit={handleCreate}>
            <h2>Add data</h2>
            <label htmlFor="id"><b>Id:</b><br /><input type="number" id='id' /></label><br />
            <label htmlFor="name"><b>Name:</b><br /><input type="text" id='name' /></label><br />
            <label htmlFor="email"><b>Email Address:</b><br /><input type="email" id='email' /></label><br />
            <label htmlFor="password"><b>Your password</b><br /><input type="password" id='password' /></label><br />
            <label htmlFor="category"><b>Category:</b><br /><input type="text" id='category' /></label><br />
            <input type="submit" value="Submit" />
          </form>
        </div>
      )}
      {read && (
        <div>
          <h2>READ</h2>
          <table>
            <thead>
              <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Email</th>
                <th>Category</th>
              </tr>
            </thead>
            <tbody>
              {items.map(item => (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.category}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
      {update && (
        <div>
          <h2>ADD</h2>
          <table>
            <thead>
              <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Email</th>
                <th>Category</th>
              </tr>
            </thead>
            <tbody>
              {items.map(item => (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.category}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
      {remove && (
        <div>
          <h2>Remove</h2>
          <table>
            <thead>
              <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Email</th>
                <th>Category</th>
              </tr>
            </thead>
            <tbody>
              {items.map(item => (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.category}</td>
                  <td><button onClick={()=>handleRemove(item.id)}>delete</button></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
