import React,{useState} from "react";
function App(){
    const [thatData,setthatData] = useState({})
    const handleChange = (e)=>{
        setthatData({...thatData,[e.target.name]:e.target})

    }

    return (
        <div>
            <input type="text" name="head" value={thatData.head} onChange={handleChange}/>
            {thatData  &&(
                <div>
                    <p> {thatData} </p>
                </div>
            )}
        </div>
    )

}
export default App







































