import React, { useState } from "react";

function AddExpense() {
    const [expense, setExpense] = useState({
        id: null,
        firstName: "",
        lastName: "",
        amount: "",
        category: ""
    });

    const handleChange = (e) => {
        setExpense({ ...expense, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch("http://localhost:8080/", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(expense)
            });

            // Handle response...
        } catch (error) {
            console.error("Error:", error);
        }
    };

    return (
        <>
            <form onSubmit={handleSubmit}>
                <input type="number" placeholder="put your id" name="id" value={expense.id} onChange={handleChange} /><br /><br />
                <input type="text" placeholder="put your first name" name="firstName" value={expense.firstName} onChange={handleChange} /><br /><br />
                <input type="text" placeholder="put your last name" name="lastName" value={expense.lastName} onChange={handleChange} /><br /><br />
                <input type="number" placeholder="put amount spent" name="amount" value={expense.amount} onChange={handleChange} /><br /><br />
                <input type="text" placeholder="put the category of expense" name="category" value={expense.category} onChange={handleChange} /><br /><br />
                <input type="submit" value="submit for storage" />
            </form>
        </>
    );
}
