import React,{useState,useEffect} from "react";

function App() {
  const url3 = "http://localhost:8080/"; 
  const [data ,setData] = useState([]);
  const [info,setInfo] = useState({
    id:null,
    firstName:"",
    lastName:"",
    amount:null,
    category:""
  })

  const [report,setReport] = useState(false);
  const handleForm = ()=>{
    setRe(!report);
  }

  const fetchInfor = () => {
    return fetch(url3)
      .then((res) => res.json())
      .then((d) => setData(d))
      .catch((error) => console.error('Error fetching data: ', error));
  }

  useEffect(() => {
    fetchInfor();
  }, []);

  return (
    <div className="App">
      <h1 style={{ color: "green" }}>Using JavaScript inbuilt FETCH API</h1>
      <button style={{color:"indigo"}}  onClick={handleForm}>Register</button>
      {report && (

        <form action="/result" method="post">
            <b>The book size:</b> <input type="number" name="size" path="size"/><br /><br />
            <b>The book color:</b> <input type="text" name="color" path="color" /><br /><br />
            <b>The bok name:</b> <input type="text" name="name" path="name" /><br /><br />
            <input type="submit" placeholder="report"/>
    </form>

)}
      <center>
        {data.length > 0 ? (
          data.map((dataObj, index) => (
            <div
              key={index}
              style={{
                width: "15em",
                backgroundColor: "#35D841",
                padding: 2,
                borderRadius: 10,
                marginBlock: 10,
              }}
            >
              <p style={{ fontSize: 20, color: 'white' }}>{dataObj.name}</p>
            </div>
          ))
        ) : (
          <p>Loading data...</p>
        )}
      </center>
    </div>
  )
}

export default App;
