import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [create, setCreate] = useState(false);
  co

  const handleCreate = () => {
    setCreate(!create); // Toggle the value of create
  }

  return (
    <div className="App">
      <header  >
      <div className="App">
        <button onClick={handleCreate}>{create ? "Hide" : "Show"} Heading</button>
        <button>Read</button>
        <button>Update</button>
        <button>Delete</button>
      </div>
      </header>

      {create && (
        <div>
          <form action="">
          <h2>Add data</h2>
          <label htmlFor="id"><b>Id:</b><input type="number" /></label><br />
          <label htmlFor="name"><b>Name:</b><input type="text" /></label><br />
          <label htmlFor="email"><b>Email Address:</b><input type="email" /></label><br />
          <label htmlFor="password"><b>Your password</b><input type="password" /></label><br />
          <label htmlFor="submit"><b>Submit this form:</b><input type="submit" placeholder='render' /></label><br />
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
