import React, { useState, useEffect } from 'react';
import axios from "axios";

function App() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    // Fetch data using axios
    axios.get('http://localhost:8080/forum-e/expense')
      .then(response => {
        // Assuming response.data is an array of items
        setItems(response.data);
      })
      .catch(error => {
        console.error('Error fetching data: ', error);
      });
  }, []); // Empty dependency array means this effect runs once after the initial render

  return (
    <div>
      <header><h1>EXPENDITURE MANIPULATION</h1></header>
      <div>
        <table>
          <thead>
            <tr>
              <th><b>ID</b></th>
              <th><b>FIRSTNAME</b></th>
              <th><b>LASTNAME</b></th>
              <th><b>AMOUNT</b></th>
              <th><b>CATEGORY</b></th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index}>
                <td>{item.id}</td>
                <td>{item.firstName}</td>
                <td>{item.lastName}</td>
                <td>{item.amount}</td>
                <td>{item.category}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
