  })

  const fetchInfor = () => {
    return fetch(url3)
      .then((res) => res.json())
      .then((d) => setData(d))
      .catch((error) => console.error('Error fetching data: ', error));
  }

  useEffect(() => {
    fetchInfor();
  }, []);

  return (
    <div className="App">
      <h1 style={{ color: "green" }}>Using JavaScript inbuilt FETCH API</h1>
      <center>
        {data.length > 0 ? (
          data.map((dataObj, index) => (
            <div
              key={index}
              style={{
                width: "15em",
                backgroundColor: "#35D841",
                padding: 2,
                borderRadius: 10,
                marginBlock: 10,
              }}
            >
              <p style={{ fontSize: 20, color: 'white' }}>{dataObj.name}</p>
            </div>
          ))
        ) : (
          <p>Loading data...</p>
        )}
      </center>
    </div>
  );
}

export default App;
