import React, { useState, useEffect } from "react";
import Axios from "axios";
import { Suspense } from "react";
import { useTranslation } from "react-i18next";


function ExpenseTable() {
    const [expenses, setExpenses] = useState([]);
    const { t,i18n} = useTranslation(); // Destructure the t function from useTranslation

    useEffect(() => {
        // Fetch data from the database when the component mounts
        fetchData();
    }, []); // Empty dependency array ensures this effect runs only once

    const fetchData = () => {
        Axios.get("http://localhost:8080/api/expenses/result")
            .then((response) => {
                setExpenses(response.data); // Assuming response.data is an array of expenses
            })
            .catch((error) => {
                console.error("Error fetching expense data: ", error);
            });
    };
    const langs = {
        en:{title:"English"},
        lug:{title:"Luganda"}
    }

    return (
        <div>
         <ul>  
        {Object.keys(langs).map((lang) => (
          <li key={lang}><button style={{ fontWeight: i18n.resolvedLanguage === lang ? 'bold' : 'normal' }} type="submit" onClick={() => i18n.changeLanguage(lang)}>
            {langs[lang].title}
          </button></li>
        ))}
        </ul>

            <table>
                <thead>
                    <tr>
                        <th>{t('main.id')}</th>
                        <th>{t('main.firstName')}</th>
                        <th>{t('main.lastName')}</th>
                        <th>{t('main.amount')}</th>
                        <th>{t('main.category')}</th>
                    </tr>
                </thead>
                <tbody>
                    {expenses.map((expense) => (
                        <tr key={expense.id}>
                            <td>{expense.id}</td>
                            <td>{expense.firstName}</td>
                            <td>{expense.lastName}</td>
                            <td>{expense.amount}</td>
                            <td>{expense.category}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default function WrapperFunction() {
    return (
        <Suspense fallback="...loading">
            <ExpenseTable />
        </Suspense>
    );
}
