import React, { useState ,useEffect} from 'react';
import axios from "axios";


function App() {
useEffect(()=>{
  console.log('we are using the useEffect hook')
})

  const [items, setItems] = useState([
    { "id": 1, "name": "ISABIRYE ELIJAH", "email": "isabiryeelijah10@gmail.com", "category": "student" },
    { "id": 2, "name": "SEMAMBO STEVEN", "email": "semasteven@gmail.com", "category": "farmer" },
    { "id": 3, "name": "MAWEJJE ISAAC", "email": "maweisac@gmail.com", "category": "businessman" },
    { "id": 4, "name": "kAMPI SHAKILA", "email": "kaskila@gmail.com", "category": "businesswoman" },
    { "id": 5, "name": "MAYINJA JOSEPH", "email": "maijoseph@gmail.com", "category": "capenter" }
  ]);

  const handleDelete = (itemId) => {
    setItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
  };

  const [add, setAdd] = useState(false);

  const handleAdd = (event) => {
    event.preventDefault();
    const id = parseInt(event.target.elements.id.value);
    const name = event.target.elements.name.value;
    const email = event.target.elements.email.value;
    const category = event.target.elements.category.value;
    const newItem = { id, name, email, category };
    setItems((prevItems) => [...prevItems, newItem]);
    setAdd(false);
  };

  const [read, setRead] = useState(false);

  const handleRead = () => {
    setRead(!read);
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th>Email</th>
            <th>Category</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.email}</td>
              <td>{item.category}</td>
              <td><button onClick={() => handleDelete(item.id)}>Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <button onClick={() => setAdd(!add)}>Add</button>
        {add && (
          <div>
            <h2>Adding a person</h2>
            <form onSubmit={handleAdd}>
              <label htmlFor="id">Id:</label>
              <input type="number" id="id" />
              <br />
              <label htmlFor="name">Name:</label>
              <input type="text" id="name" />
              <br />
              <label htmlFor="email">Email:</label>
              <input type="email" id="email" />
              <br />
              <label htmlFor="category">Category:</label>
              <input type="text" id="category" />
              <br />
              <input type="submit" value="Submit" />
            </form>
          </div>
        )}
      </div><br />
      <div>
        <button onClick={handleRead}>Read</button>
      </div>
    </div>
  );
}

export default App;
