import React, { useState } from 'react';

function App() {
    const [formData, setFormData] = useState({
        id: '',
        firstName: '',
        lastName: '',
        amount: '',
        category: ''
    });

    const [submittedData, setSubmittedData] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setSubmittedData(formData);
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <b>your id       :</b><input type="number" name="id" value={formData.id} onChange={handleChange} /><br/><br/>
                <b>your first name:</b><input type="text" name="firstName" value={formData.firstName} onChange={handleChange} /><br/><br/>
                <b>your last name :</b><input type="text" name="lastName" value={formData.lastName} onChange={handleChange} /><br/><br/>
                <b>your amount    :</b><input type="number" name="amount" value={formData.amount} onChange={handleChange} /><br/><br/>
                <b>your category:</b><input type="text" name="category" value={formData.category} onChange={handleChange} /><br/><br/>
                <input type="submit" value="submit" onClick={handlePress}/>
            </form>

            {submittedData && (
                <div>
                    <h1>The Results....</h1>
                    <label htmlFor="id">ID: {submittedData.id}</label><br/><br/>
                    <label htmlFor="firstName">First Name: {submittedData.firstName}</label><br/><br/>
                    <label htmlFor="lastName">Last Name: {submittedData.lastName}</label><br/><br/>
                    <label htmlFor="amount">Amount: {submittedData.amount}</label><br/><br/>
                    <label htmlFor="category">Category: {submittedData.category}</label><br/><br/>
                </div>
            )}
        </div>
    );
}

export default App;
