import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [create, setCreate] = useState(false);

  const handleCreate = () => {
    setCreate(!create); // Toggle the value of create
  }

  return (
    <div className="App">
      <header  >
      <div className="App">
        <button onClick={handleCreate}>{create ? "Hide" : "Show"} Heading</button>
        <button>Read</button>
        <button>Update</button>
        <button>Delete</button>
      </div>
      </header>

      {create && (
        <div>
          <form action="">
          <h2>Add data</h2>
          <label htmlFor=""><input type="text" /></label><br />
          <label htmlFor=""><input type="text" /></label><br />
          <label htmlFor=""><input type="text" /></label><br />
          <label htmlFor=""><input type="text" /></label><br />
          <label htmlFor="submit"><b>Submit this form:</b><input type="sub" /></label><br />
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
